CREATE TABLE reg (reg_id INT NOT NULL AUTO_INCREMENT, fname VARCHAR(70), mname VARCHAR(70), sname VARCHAR(70), pnumber VARCHAR(70), onumber VARCHAR(70), email VARCHAR(100), password VARCHAR(50), birth_date VARCHAR(45), country VARCHAR(50), address VARCHAR(300), `state` VARCHAR(50), town VARCHAR(70), paddress VARCHAR(300), sex VARCHAR(45), mstatus VARCHAR(70), job VARCHAR(100), passport VARCHAR(45), PRIMARY KEY (reg_id));
